  @Test public void testAdd () {
    BooleanList list = new BooleanList();
    boolean []  arr  = new boolean[1000];
    boolean element = false;
    for (int i = 0 ; i!= 1000; ++i) {
      element = rand.nextBoolean();
      list.add(element);
      arr[i] = element;
    }
    assertEquals(1000, list.size());
    assertTrue(arrEquals(arr, list.toArray()));
  }

  @Test public void testAddIdx() {
    BooleanList list = new BooleanList();
    for (int i = 0; i!=50; ++i) {
      list.add(rand.nextBoolean());
    }
    boolean val  = false;
    boolean test = list.get(0);
    list.add(0, val);
    assertEquals(val, list.get(0));
    assertEquals(test, list.get(1));

    int i = list.size()-1;
    test = list.get(i);
    list.add(i, val);
    assertEquals(val, list.get(i));
    assertEquals(test, list.get(i+1));

    i = list.size()/2;
    test = list.get(i);
    list.add(i, val);
    assertEquals(val, list.get(i));
    assertEquals(test, list.get(i+1));
  }

  @Test public void testAddAllIdx() {
    BooleanList list = new BooleanList();
    BooleanList list2 = new BooleanList();
  
    boolean val = false;
    list2.add(val);
    list2.add(val);
    list2.add(val);
    

    for (int i = 0; i!=50; ++i) {
      list.add(rand.nextBoolean());
    }
    int osz = list.size();

    list.addAll(0, list2);
    assertEquals(osz+list2.size(), list.size());
    assertEquals(false, list.get(0));

    val = list.get(list.size()-1);
    list.addAll(list.size()-1, list2);
    assertEquals(val, list.get(list.size()-1));
    
  }

  @Test public void testContainsAll() {
    BooleanList list = new BooleanList();
    for (int i = 0 ; i!= 1000; ++i) {
      list.add(rand.nextBoolean());
    }
    BooleanList list2 = list.subList(250,500);
    assertTrue(list.containsAll(list2));
  }

  @Test public void testRemoveIdx() {
    BooleanList list = new BooleanList();
    for (int i = 0 ; i!= 1000; ++i) {
      list.add(rand.nextBoolean());
    }
    int osz = list.size();
    boolean oval1 = list.get(list.size()-1);
    boolean oval2 = list.removeIdx(list.size()-1);
    assertEquals(osz-1, list.size());
    assertEquals(oval1, oval2);

    oval1 = list.get(0);
    oval2 = list.removeIdx(0);

    assertEquals(osz-2, list.size());
    assertEquals(oval1, oval2);
     
  }

  

