import java.util.Random;

public class CharStatList extends CharList {

  public void each (CharFunction func) {
    for(int i=0; i!=this.size(); ++i) {
      func.apply(this.get(i));
    }  
  }

  public char reduce(char initial, CharFunction func) {
    char memo = initial;
    for(int i=0; i!=this.size(); ++i) {
      memo = func.accumulate(memo, this.get(i));
    }
    return memo;
    
  } 

  public char sum () {
    CharFunction f = new CharFunction() {
      public char accumulate(char memo, char value) {
        return (char)(memo+value);
      }
    };

    return this.reduce((char)0,f);
  }

  public CharStats stats () {
    
    final CharStats stats = new CharStats();

    CharFunction f = new CharFunction() {
      public void apply(char value) {
        
        if (value < stats.min) {
          stats.min = value;
        } 
        if (value > stats.max) {
          stats.max = value;
        }
        stats.sum += value;
      }
    
    };
     
    this.each(f);
    
    stats.n   = this.size();
    stats.avg = stats.sum / (double)stats.n;
    
    f = new CharFunction() {
      public void apply(char value) {
        stats.var += Math.pow(stats.avg - (double)value, 2);
      }
    };
    
    this.each(f);

    stats.var  = stats.var / (double)stats.n;
    stats.stdd = Math.pow(stats.var, 0.5);
    return stats;

    
  }

  static CharStatList random(int count) {
    Random       rand = new Random(0L);
    CharStatList slist = new CharStatList();
    char value;
    for (int i=0; i!=count; ++i) {
      value = (char)rand.nextInt();
      slist.add(value);
    }
    return slist;
  }

  class CharStats {
    public int n;
    public char min = Character.MAX_VALUE;
    public char max = Character.MIN_VALUE;
    public char sum;
    public double avg;
    public double stdd;
    public double var;
    public String toString() {
      StringBuilder b = new StringBuilder();
      b.append("n    = "); b.append(n);   b.append("\n");
      b.append("min  = "); b.append(min); b.append("\n");
      b.append("max  = "); b.append(max); b.append("\n");
      b.append("sum  = "); b.append(sum); b.append("\n");
      b.append("avg  = "); b.append(avg); b.append("\n");
      b.append("stdd = "); b.append(stdd);b.append("\n");
      b.append("var  = "); b.append(var); b.append("\n");
      return b.toString();
    }
  }
  
  private static void p(Object o) {
    System.out.println(o);
  }
  public static void main (String [] args) {
    CharStatList list = CharStatList.random(10);
    
    list.each(new CharFunction() {
      public void apply(char value) {
        p(value);
      }
    });
    p(list.stats());
    p(list.sum());

    IntStatList l = new IntStatList();
    for (int i =1; i<101; ++i) {
      l.add(i);
    }
    p(l.stats());
    p(l.sum());
  }

}

